// src/App.js
import React from 'react';
import styled from 'styled-components';

const Container = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  height: 100vh;
  background-color: #8a2be2; // Cor de fundo roxa
`;

const TaskCard = styled.div`
  background-color: #1c1c2e; // Fundo do card
  padding: 20px;
  border-radius: 10px;
  box-shadow: 0 4px 8px rgba(0, 0, 0, 0.2);
  color: white;
  width: 300px;
  text-align: center;
`;

const Title = styled.h2`
  margin-bottom: 20px;
`;

const TaskItem = styled.div`
  background-color: #2b2b44; // Fundo da tarefa
  padding: 10px;
  margin: 10px 0;
  border-radius: 5px;
  display: flex;
  justify-content: space-between;
  align-items: center;
`;

const Button = styled.button`
  background-color: #8a2be2; // Cor do botão
  border: none;
  padding: 10px;
  border-radius: 5px;
  color: white;
  cursor: pointer;

  &:hover {
    background-color: #7a1fe2; // Cor ao passar o mouse
  }
`;

const App = () => {
  return (
    <Container>
      <TaskCard>
        <Title>Terça-Feira, 24 de Julho</Title>
        <TaskItem>
          <input type="checkbox" />
          <span>Começar a execução do projeto</span>
          <div>
            <Button>Edit</Button>
            <Button>Delete</Button>
          </div>
        </TaskItem>
        <Button>Nova tarefa</Button>
      </TaskCard>
    </Container>
  );
}

export default App;
